import React, { useState, useRef, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
} from '@mui/material';
import UploadFileIcon from '@mui/icons-material/UploadFile';

function App() {
  const [messages, setMessages] = useState([]);
  const [connection, setConnection] = useState(null);
  const mediaRecorder = useRef(null);
  const audioChunks = useRef([]);
  const isRecording = useRef(false);

  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:8000/ws');
    setConnection(ws);

    ws.onmessage = async (event) => {
      try {
        const response = JSON.parse(event.data);
        setMessages(prev => [...prev, { type: 'bot', text: response.text }]);

        if (response.audio) {
          const audio = new Audio(response.audio);
          await audio.play().catch(console.error);
          // Ses çalma bittikten sonra tekrar kayda başla
          startRecording();
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
        if (typeof event.data === 'string') {
          setMessages(prev => [...prev, { type: 'error', text: event.data }]);
        }
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setMessages(prev => [...prev, { type: 'error', text: 'Bağlantı hatası oluştu.' }]);
    };

    ws.onclose = () => {
      setConnection(null);
      setMessages(prev => [...prev, { type: 'system', text: 'Bağlantı kapandı.' }]);
    };
  };

  const startRecording = async () => {
    if (isRecording.current) return;

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorder.current = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus'
      });
      audioChunks.current = [];
      isRecording.current = true;

      mediaRecorder.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunks.current.push(event.data);
        }
      };

      mediaRecorder.current.onstop = async () => {
        isRecording.current = false;
        const audioBlob = new Blob(audioChunks.current, { type: 'audio/webm;codecs=opus' });
        const reader = new FileReader();

        reader.onload = async () => {
          if (connection && connection.readyState === WebSocket.OPEN) {
            connection.send(reader.result);
          } else {
            setMessages(prev => [...prev, { type: 'error', text: 'WebSocket bağlantısı kapalı.' }]);
            startRecording(); // Bağlantı hatası durumunda tekrar dene
          }
        };

        reader.readAsDataURL(audioBlob);
      };

      // Her 3 saniyede bir kaydı durdurup yeni kayıt başlat
      mediaRecorder.current.start();
      setTimeout(() => {
        if (mediaRecorder.current && mediaRecorder.current.state === 'recording') {
          mediaRecorder.current.stop();
          mediaRecorder.current.stream.getTracks().forEach(track => track.stop());
        }
      }, 3000);

    } catch (error) {
      console.error('Error accessing microphone:', error);
      setMessages(prev => [...prev, { type: 'error', text: 'Mikrofona erişim hatası: ' + error.message }]);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/upload-database', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setMessages(prev => [...prev, { type: 'system', text: 'Veritabanı başarıyla yüklendi' }]);
      } else {
        const errorText = await response.text();
        setMessages(prev => [...prev, { type: 'error', text: 'Veritabanı yükleme hatası: ' + errorText }]);
      }
    } catch (error) {
      console.error('Error uploading database:', error);
      setMessages(prev => [...prev, { type: 'error', text: 'Veritabanı yükleme hatası: ' + error.message }]);
    }
  };

  useEffect(() => {
    connectWebSocket();

    // Hoşgeldin mesajı için yeni WebSocket bağlantısı
    const welcomeWs = new WebSocket('ws://localhost:8000/ws');
    welcomeWs.onopen = () => {
      welcomeWs.send(JSON.stringify({ type: 'welcome' }));
    };

    welcomeWs.onmessage = async (event) => {
      try {
        const response = JSON.parse(event.data);
        setMessages([{ type: 'bot', text: response.text }]);

        if (response.audio) {
          const audio = new Audio(response.audio);
          await audio.play().catch(console.error);
          // Hoşgeldin mesajı bittikten sonra kayda başla
          startRecording();
        }
      } catch (error) {
        console.error('Welcome message error:', error);
      }
      welcomeWs.close();
    };

    return () => {
      if (connection) {
        connection.close();
      }
      welcomeWs.close();
      if (mediaRecorder.current) {
        mediaRecorder.current.stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const getMessageColor = (type) => {
    switch (type) {
      case 'bot':
        return 'primary.main';
      case 'error':
        return 'error.main';
      case 'system':
        return 'text.secondary';
      default:
        return 'text.primary';
    }
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Türkçe Sesli Asistan
        </Typography>

        <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
          <input
            accept=".json"
            style={{ display: 'none' }}
            id="database-file"
            type="file"
            onChange={handleFileUpload}
          />
          <label htmlFor="database-file">
            <Button
              variant="contained"
              component="span"
              fullWidth
              startIcon={<UploadFileIcon />}
            >
              Veritabanı Yükle
            </Button>
          </label>
        </Paper>

        <Paper elevation={3} sx={{ p: 2, mb: 2, minHeight: '300px', maxHeight: '500px', overflow: 'auto' }}>
          <List>
            {messages.map((message, index) => (
              <ListItem key={index}>
                <ListItemText
                  primary={message.text}
                  secondary={message.type === 'bot' ? 'Asistan' : message.type === 'system' ? 'Sistem' : 'Hata'}
                  sx={{ color: getMessageColor(message.type) }}
                />
              </ListItem>
            ))}
          </List>
        </Paper>
      </Box>
    </Container>
  );
}

export default App;
